targets.expr:
sort_desc(
  max by(id, name) (
    60 * grafanacloud_instance_samples_per_second{id=~"$metrics_id"}
    / grafanacloud_instance_active_series{id=~"$metrics_id"}

    * on(id) group_left(name) grafanacloud_instance_info{id=~"$metrics_id"}
  )
)

sort_desc(
  sum_over_time(
    sum by (id) (
      grafanacloud_logs_instance_query_bytes:rate5m{id=~"$logs_id"}
    ) [$__range:1m]
  ) /
  sum_over_time(
    sum by (id) (
      grafanacloud_logs_instance_billable_bytes_received_per_second{id=~"$logs_id"}
    ) [$__range:1m]
  )
) * on(id) group_left(name) grafanacloud_logs_instance_info{id=~"$logs_id"}
